function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".menu"),o=document.querySelector("body");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),o.classList.remove("locked")):(c.classList.add("active"),e.classList.add("active-burger"),o.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(c.classList.remove("active"),e.classList.remove("active-burger"),o.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const close=document.querySelector(".close__img"),covid=document.querySelector(".sections");function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const c=e.parentNode;c.classList.contains("accordion__item-active")?c.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),c.classList.add("accordion__item-active"))}))}))}close.onclick=function(){covid.style.display="none"},accordion();